import React  from 'react';
import {render} from 'react-dom';
// const module_name = require('../../src/index.js');
import Module_name from '../../src/index.js';
import list from './markers.js';
import linePath from './linePath.js';
let defaultProps = {

	wraperStyle : {
		width : '500px',
		height : '500px',
		position : 'relative',
		float : 'left'
	},
	popStyle : {
		width : '800px',
		height : '800px',
		position : 'absolute',
		display : 'inline'
	},
	zoom : 8,
	linePath :[],
	markers : list,
	markerSelIndex : -1,
	preHref : 'https://www-test.yundijie.com/webapp/hotel/detail.html?hotelId='
}
function getLi(){
	let list = [];
	for(var i = 0 ;i<10;i++){
		list.push(<li key={i} onMouseOver={mouseOver.bind(this,i)} style={{padding : '20px 0'}}>index</li>)
	}
	return list;
}
function mouseOver(index){
	console.log(index);
	defaultProps.markerSelIndex = index;
	Render();
}
function Render(){
	render(
		<div>
			<Module_name
				{...defaultProps}
				>
				<div style={{'backgroundColor':'red',position :'absolute'}}>123123</div>
				<div style={{'backgroundColor':'red',position :'absolute'}}>1231</div>
			</Module_name>
			<ul style={{float:'right',fontSize:'20px',width:'200px'}}>
				{getLi()}
			</ul>
		</div>,

		document.querySelector('#wrap')
	)
}

Render();
